%spark.ipyspark

# support functions

import re


def normalize_movies(line):
  try:
    movieId, title, year, genres = re.split(r"(\d+),\"?(.+)\((\d+)\)[ ]*\"?,(.+)", line)[1:5]
    movieId = int(movieId)
    title = title.strip()
    year = None if len(year) < 2 else int(year)
    genres = [genre.strip() for genre in genres.split('|')]
    return (movieId, title, year, genres)
  except:
    return (None, None, None, None)


def normalize_ratings(line):
  try:
    movieId, rating = re.split(r"(\d+),(\d+),(\d{1}.\d{1}),(\d+)", line)[2:4]
    movieId = int(movieId)
    rating = float(rating)
    return (movieId, rating)
  except:
    return (None, None)


def transform_movies_1(line):
    movies_list = []
    for genre in line[3]:
        movies_list.append((line[0], line[1], line[2], genre))
    return movies_list


def transform_movies_2(line):
    movieId = line[0]
    title = line[1]
    year = line[2]
    genre = line[3]
    return (movieId, (title, year, genre))


def normalize_merge_movies(line):
    genre = line[1][0][2]
    title = line[1][0][0]
    year = line[1][0][1]
    rating = line[1][1]
    return (genre, (title, year, rating))


def sort_movies(line):
    genre = line[0]
    films = sorted((list(line[1])), key=lambda line: line[2], reverse=True)
    return (genre,(films))


def top_n_movies(line, N):
    genre = line[0]
    films = line[1][0:N]
    return (genre, films)


def csv_transform(line):
    csv_list = []
    for element in line[1]:
        csv_list.append((line[0], element[0], element[1], element[2]))
    return csv_list


def to_csv(rdd):
    return ','.join(str(line) for line in rdd)
